mapper.insert:
/** BbsMapper.getBoard **/ 
INSERT INTO BBS_MST
(
	  PARENT_BBS_SEN
	, BBS_TY_CD
	, BBS_NAE
	, BBS_DESCRIPT
	, SORT_ORD
	, INP_ID
)
VALUES
(
	  #{parentBbsSen}
	, #{bbsTyCd}
	, #{bbsNae}
	, #{bbsDescript}
	, #{sortOrd}
	, #{inpId}
)

	SELECT MAX(BBS_SEN) AS bbsSen
	FROM BBS_MST

mapper.update:
/** BbsMapper.updateBbsMst **/

UPDATE BBS_MST SET
	BBS_TY_CD = #{bbsTyCd}
	, BBS_NAE = #{bbsNae}
	, BBS_DESCRIPT = #{bbsDescript}
	, SORT_ORD = #{sortOrd}
	, UPD_ID = #{updId}
	, UPD_DT = NOW()
WHERE BBS_SEN = #{bbsSen}

mapper.insert:
/** BbsMapper.insertBbsOptn **/ 
INSERT INTO BBS_OPTN
(
	  BBS_SEN
	, GDNICE_USE_YN
	, GDNICE_SCR
	, GDNICE_UPP_NUM
	, SCRTARTC_FNCT_YN
	, ANSER_FNCT_YN
	, ANSWER_FNCT_YN
	, ANNYMTY_FNCT_YN
	, STTEMNT_FNCT_YN
	, STTEMNT_FNCT_TS_NUM
	, INP_ID
)
VALUES
(
	  #{bbsSen}
	, #{gdniceUseYn}
	, #{gdniceScr}
	, #{gdniceUppNum}
	, #{scrtartcFnctYn}
	, #{anserFnctYn}
	, #{answerFnctYn}
	, #{annymtyFnctYn}
	, #{sttemntFnctYn}
	, #{sttemntFnctTsNum}
	, #{inpId}
)


	SELECT MAX(OPTN_SEN) AS optnSen
	FROM BBS_OPTN

mapper.update:
/** BbsMapper.updateBbsOptn **/

UPDATE BBS_OPTN SET
	  GDNICE_USE_YN = #{gdniceUseYn}
	, GDNICE_SCR = #{gdniceScr}
	, GDNICE_UPP_NUM = #{gdniceUppNum}
	, SCRTARTC_FNCT_YN = #{scrtartcFnctYn}
	, ANSER_FNCT_YN = #{anserFnctYn}
	, ANSWER_FNCT_YN = #{answerFnctYn}
	, ANNYMTY_FNCT_YN = #{annymtyFnctYn}
	, STTEMNT_FNCT_YN = #{sttemntFnctYn}
	, STTEMNT_FNCT_TS_NUM = #{sttemntFnctTsNum}
	, UPD_ID = #{updId}
	, UPD_DT = NOW()
WHERE BBS_SEN = #{bbsSen}
AND OPTN_SEN = #{optnSen}

mapper.delete:
/** BbsMapper.deleteBbsMst **/
DELETE FROM BBS_MST
WHERE BBS_SEN = #{bbsSen}

/** BbsMapper.deleteBbsMst **/
DELETE FROM BBS_OPTN
WHERE OPTN_SEN = #{optnSen}
